import { Link } from "react-router-dom";
import "./index.scss";
import { useState } from "react";
import Axios from "axios";
import "../LoginPage/index.scss";

function LoginPage() {
  const [data, setData] = useState({
    email: "",
    password: "",
  });

  const [error, setError] = useState("");

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const url = "http://localhost:3001/api/auth";
      const { data: res } = await Axios.post(url, data);
      localStorage.setItem("token", res.data);
      window.location = "/user";
      console.log(res.message);
    } catch (error) {
      if (error.response.status >= 400 && error.response.status <= 500) {
        setError(error.response.data.message);
      }
    }
  };

  return (
    <div className="container">
      <h1 className="text-center mt-4">Start blogging your codes</h1>
      <form onSubmit={handleSubmit}>
        <h1></h1>
        <div className="inset">
          <label htmlFor="email">EMAIL ADDRESS</label>
          <input
            type="text"
            name="email"
            id="email"
            onChange={handleChange}
            value={data.email}
          />
          <label htmlFor="password">PASSWORD</label>
          <input
            type="password"
            name="password"
            id="password"
            onChange={handleChange}
            value={data.password}
          />
          <div className="d-flex">
            <input type="checkbox" name="remember" id="remember" />
            <label htmlFor="remember">Remember me for 14 days</label>
          </div>
          <div className="p-container text-center mt-5">
            <input type="submit" name="go" id="go" value="Let me in" />
          </div>
          {error && <div className="error_msg">{error}</div>}

          <div className="text-center">
            <Link to="/reset-password" className="btn">
              Forgot Password?
            </Link>
          </div>
          <div className="text-center">
            <Link to="/register" className="btn">
              New here? Create an account
            </Link>
          </div>
        </div>

        <div className="hr">
          <div></div>
          <div>OR</div>
          <div></div>
        </div>
        <div className="d-flex justify-content-center mb-5">
          <div className="google-btn">
            <div className="google-icon-wrapper">
              <img
                className="google-icon"
                src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg"
                alt="google-icon"
              />
            </div>
            <p className="btn-text">
              <b>Sign in with google</b>
            </p>
          </div>
        </div>
      </form>
    </div>
  );
}

export default LoginPage;
